import random

def row_strategy(history):
    if not history:
        return random.choice([0, 1])

    # Simulate MCTS
    num_simulations = 100
    scores = {0: 0, 1: 0}

    for _ in range(num_simulations):
        simulated_history = history[:]
        while len(simulated_history) < 10:
            my_move = random.choice([0, 1])
            their_move = simulated_history[-1][1] if simulated_history else random.choice([0, 1])
            simulated_history.append((my_move, their_move))

        if simulated_history[-1][0] == 0 and simulated_history[-1][1] == 0:
            scores[0] += 2
        elif simulated_history[-1][0] == 1 and simulated_history[-1][1] == 1:
            scores[1] += 2

    return max(scores, key=scores.get)

