import random

def row_strategy(history):
    if not history:
        return random.randint(0, 1)

    # Extract opponent's moves
    their_moves = [move for _, move in history]

    # Simple belief update: assume opponent copies previous move with 80% probability
    # and chooses randomly otherwise (20%)
    if len(their_moves) == 1:
        # First move: random
        return random.randint(0, 1)

    # Model opponent as copying last move with 80% probability
    last_move = their_moves[-1]
    # With 80% chance, opponent copies last move; 20% chance, random
    if random.random() < 0.8:
        return last_move
    else:
        return random.randint(0, 1)
