import random

def row_strategy(history):
    if len(history) < 5:
        return random.choice([0, 1])

    # Extract the last 5 rounds of opponent's moves
    their_moves = [entry[1] for entry in history[-5:]]

    # Compute cooperation score: proportion of times opponent matched row player's move
    cooperation_score = 0
    for i in range(5):
        if their_moves[i] == history[-(i+1)][0]:
            cooperation_score += 1

    cooperation_score /= 5.0

    if cooperation_score > 0.6:
        # Coordinate with 90% probability
        return random.choices([0, 1], weights=[0.9, 0.1])[0]
    else:
        # Symmetric random choice (50-50)
        return random.choice([0, 1])
