        for i in range(1, len(their_moves)):
            if their_moves[i] == their_moves[i-1]:
                alternating = False
                break

        if alternating:
            # Opponent alternates; break the pattern with 70% chance
            return 1 if their_moves[-1] == 0 else 0 if random.random() < 0.7 else 0
