import random

def row_strategy(history):
    if len(history) < 2:
        return random.choice([0, 1])

    # Extract the last two opponent moves
    their_last_two = [move for _, move in history[-2:]]

    # Base probability is 0.5
    prob_0 = 0.5
    prob_1 = 0.5

    # If opponent repeated the same move twice in a row, respond with the opposite
    if their_last_two[0] == their_last_two[1]:
        # Increase probability of choosing the opposite action
        if their_last_two[0] == 0:
            prob_1 += 0.1
        else:
            prob_0 += 0.1

    # Normalize probabilities
    total = prob_0 + prob_1
    if total > 0:
        prob_0 /= total
        prob_1 /= total

    # Choose action based on updated probabilities
    if random.random() < prob_0:
        return 0
    else:
        return 1
