import random

def row_strategy(history):
    if len(history) == 0:
        return 0

    # Initialize belief: probability that column player prefers coordination (both 0)
    belief_coordination = 0.5

    # Update belief based on history
    for (my_move, their_move) in history:
        if my_move == 0 and their_move == 0:
            belief_coordination += 0.1
        elif my_move == 1 and their_move == 1:
            belief_coordination += 0.1
        else:
            belief_coordination -= 0.05  # Misalignment reduces coordination belief

    # Normalize belief to [0, 1]
    belief_coordination = max(0.0, min(1.0, belief_coordination))

    # Set coordination probability based on belief
    if belief_coordination > 0.7:
        return 0
    else:
        return 1
