import random

def row_strategy(history):
    if not history:
        return random.choice([0, 1])

    # Extract opponent's moves
    opponent_moves = [move for _, move in history]

    # Use a simple majority rule: pick the most frequent move
    if len(opponent_moves) == 0:
        return random.choice([0, 1])

    move_count = {0: 0, 1: 0}
    for move in opponent_moves:
        move_count[move] += 1

    # Return the move with higher frequency; random if tied
    if move_count[0] >= move_count[1]:
        return 0
    else:
        return 1
