def row_strategy(history):
    if not history:
        return 0

    # Calculate average payoff for each action (0 or 1)
    total_payoff_0 = 0
    total_payoff_1 = 0
    for my_move, their_move in history:
        if my_move == 0 and their_move == 0:
            total_payoff_0 += 2
        elif my_move == 1 and their_move == 1:
            total_payoff_1 += 2
        # Otherwise, both different → 0 payoff
    n = len(history)
