    avg_0 = total_payoff_0 / n if total_payoff_0 > 0 else 0
    avg_1 = total_payoff_1 / n if total_payoff_1 > 0 else 0

    # Choose action with higher average payoff
    if avg_0 > avg_1:
        return 0
    else:
