import random

def row_strategy(history):
    if len(history) == 0:
        return 0

    # Count how many times the column player chose 1 (Coordination)
    their_ones = sum(move[1] for move in history)
    their_total = len(history)

    # If column player chose 1 in more than 60% of rounds, increase chance of choosing 0
    if their_ones > 0.6 * their_total:
        # Bias toward 0 (to counter their coordination)
        return 0 if random.random() < 0.7 else 1
    else:
        # Otherwise, random choice
        return random.randint(0, 1)
