import random
import numpy as np

def row_strategy(history):
    if not history:
        return 0

    # Extract opponent's moves
    opponent_moves = [move for _, move in history]

    # Calculate variance of opponent's moves
    if len(opponent_moves) == 1:
        variance = 0
    else:
        mean = np.mean(opponent_moves)
        variance = np.var(opponent_moves)

    # Count moves to detect pattern
    ones_count = opponent_moves.count(1)
    zeros_count = opponent_moves.count(0)

    # If variance is high (opponent inconsistent), bias toward most common action (80-20)
    if variance > 0.3:
        if ones_count > zeros_count:
            return random.choices([0, 1], weights=[20, 80])[0]
        else:
            return random.choices([0, 1], weights=[80, 20])[0]
    # If variance is low (opponent consistent), use balanced 50-50 split
    elif variance < 0.1:
        return random.choices([0, 1], weights=[50, 50])[0]
    # Otherwise, use 70-30 bias toward 0 (since we prefer 0)
    else:
        if ones_count > zeros_count:
            return random.choices([0, 1], weights=[30, 70])[0]
        else:
            return random.choices([0, 1], weights=[70, 30])[0]
