import random

def row_strategy(history):
    if not history:
        return 0

    # Count frequencies of past moves
    move_counts = {0: 0, 1: 0}
    for _, move in history:
        move_counts[move] += 1

    # Determine decay factor based on round number (first 10 rounds: 95% to 90%)
    round_num = len(history)
    if round_num <= 10:
        decay_factor = 0.95 - 0.05 * (round_num - 1)  # Starts at 0.95, ends at 0.90 after 10 rounds
    else:
        decay_factor = 0.90  # After 10 rounds, stabilize at 90%

    # Base probability based on frequency (inverse frequency)
    total = sum(move_counts.values())
    if total == 0:
        return 0

    prob_0 = 1.0 / (move_counts[0] + 1) * decay_factor
    prob_1 = 1.0 / (move_counts[1] + 1) * decay_factor

    # Normalize probabilities
    total_prob = prob_0 + prob_1
    if total_prob == 0:
        return 0

    prob_0 /= total_prob
    prob_1 /= total_prob

    # Random selection
    if random.random() < prob_0:
        return 0
    else:
        return 1
