import random

def row_strategy(history):
    if not history:
        return 0

    # Count consecutive matches where opponent's move aligned with our last move
    consecutive_matches = 0
    for i in range(len(history) - 1):
        our_last, their_last = history[i]
        if our_last == history[i+1][0]:
            if history[i+1][1] == their_last:
                consecutive_matches += 1

    # Base strategy: start with 0, but adjust based on opponent's pattern
    # If opponent has been copying our move (80% chance), we should deviate to avoid coordination
    if consecutive_matches > 3:
        # High consistency: randomize to break pattern
        return random.randint(0, 1)
    elif len(history) > 5:
        # Medium history: favor 0 (our preference) unless opponent shows strong pattern
        # Check if opponent tends to mirror
        their_moves = [move for _, move in history]
        our_moves = [move for move, _ in history]

        # If opponent's move matches our last move more than 70% of the time, deviate
        mirror_count = sum(1 for i in range(1, len(their_moves)) if their_moves[i] == our_moves[i-1])
        if mirror_count > 0.7 * (len(their_moves) - 1):
            return 1
        else:
            return 0
    else:
        # Short history: default to 0
        return 0
ENDOFFILE

