import random

def row_strategy(history):
    if not history:
        return 0

    # Count consecutive matches where opponent's move aligned with our last move
    consecutive_matches = 0
    for i in range(len(history) - 1):
        our_last, their_last = history[i]
        if our_last == history[i+1][0]:
            if history[i+1][1] == their_last:
                consecutive_matches += 1

    # Base strategy: start with 0, but adjust based on opponent's pattern
    # Use exponential scaling: confidence = 1.1^k where k is consecutive matches
    confidence_factor = 1.0
    if consecutive_matches > 0:
        confidence_factor = 1.1 ** consecutive_matches

    # If opponent has been consistently mirroring (high confidence), deviate
    if len(history) > 5:
        their_moves = [move for _, move in history]
        our_moves = [move for move, _ in history]

        # Count mirror instances (opponent copies our last move)
        mirror_count = sum(1 for i in range(1, len(their_moves)) if their_moves[i] == our_moves[i-1])
        mirror_ratio = mirror_count / (len(their_moves) - 1) if len(their_moves) > 1 else 0

        # If mirror ratio exceeds 70%, deviate to 1 to avoid payoff loss
        if mirror_ratio > 0.7 and confidence_factor > 1.5:
            return 1
        else:
            return 0
    else:
        # Short history: default to 0
        return 0
ENDOFFILE

