import random
def row_strategy(history):
    if not history:
        return 0

    # Compute a 3-round moving average of row player's payoff in cooperative outcomes
    # Cooperative outcomes are when both players choose the same action (0,0) or (1,1)
    # Payoff for row player: 2 in (0,0), 1 in (1,1), 0 otherwise
    cooperative_payoffs = []
    for my_move, their_move in history:
        if my_move == their_move:
            if my_move == 0:
                cooperative_payoffs.append(2)
            else:
                cooperative_payoffs.append(1)

    # If fewer than 3 cooperative rounds, use a simple baseline
    if len(cooperative_payoffs) < 3:
        return 0

    # Compute 3-round moving average
    moving_avg = sum(cooperative_payoffs[-3:]) / 3

    # If moving average is high (suggesting sustained cooperation), favor 0
    # Otherwise, follow opponent's last move to exploit copying behavior
    if moving_avg > 1.2:
        return 0
    else:
        return history[-1][1]
