import random
import math

def row_strategy(history):
    if not history:
        return 0

    # Initialize expected payoffs and parameters
    expected_payoff_0 = 1.0
    expected_payoff_1 = 1.0
    learning_rate = 0.01

    # Time step (number of rounds so far)
    t = len(history)

    # Time-varying learning rate: α(t) = 0.01 * exp(-t/100)
    learning_rate = 0.01 * math.exp(-t / 100.0)

    # Update expected payoffs based on past interactions
    for (my_move, their_move) in history:
        if my_move == 0 and their_move == 0:
            expected_payoff_0 += learning_rate * 1.0
        elif my_move == 1 and their_move == 1:
            expected_payoff_1 += learning_rate * 1.0
        elif my_move == 0 and their_move == 1:
            expected_payoff_0 += learning_rate * 0.0
        elif my_move == 1 and their_move == 0:
            expected_payoff_1 += learning_rate * 0.0

    # Decide move based on expected payoffs
    if expected_payoff_0 > expected_payoff_1:
        return 0
    else:
        return 1
